function CDialog(title, parentDlg)
{
	if (title)
		this.m_title = title;
	else
		this.m_title = null;

	this.zind = (parentDlg) ? parentDlg.zind+1 : 21 + gDialogsVisisble; // put new dialogs on top of previous ones
	this.m_initialized = false;
	this.parentDlg = (parentDlg) ? parentDlg : null;
	this.f_close = false;
	//this.initdlg();
}

