	var row = alib.dom.createElement("tr", tbody);
	var td_lbl = alib.dom.createElement("td", row);
	alib.dom.styleSetClass(td_lbl, "formLabel");
	alib.dom.styleSet(td_lbl, "width", "55px");
	td_lbl.innerHTML = "Type:";
