OlapCube_Table_Matrix.prototype.getColMeasure = function(data, val, measures)
{
	ret = 0;

	// check to see if 'val' is
	// an array and then dig deeper if it is and print a cell for each end value

	if (data[val])
	   ret = data[val][measures[0]];


	return ret;
}

