	rw.addCell(act);
	btn = new CButton("open", function(cls, wfid, obj_type) { cls.openWorkflow(wfid, obj_type); }, [this, id, obj_type], "b2");
	rw.addCell(btn.getButton());
	rw.addCell(del_dv, true, "center");
