    alib.dom.styleSet(approveLink, "cursor", "pointer"); 
    alib.dom.styleSet(approveLink, "color", "#0000FF"); 
    
    var spacer = alib.dom.setElementAttr(alib.dom.createElement("span"), [["innerHTML", " | "]]);
    
