	{
		var dv = ALib.m_document.createElement("div");
		dv.m_widcls = this;
		dv.onclick = function() { this.m_widcls.setData(); };
		alib.dom.styleSet(dv, "cursor", "pointer");
		dv.innerHTML = "Click here to set RSS url";
		this.m_container.appendChild(dv);
	}
	else
		this.loadRss();
